import { BrowserRouter, Route, Routes } from 'react-router-dom';
export const VentanaBase=(idrl)=>{
    
    return (
        <div className="h-screen flex flex-col">
          <div className="bg-gray-800 text-white p-4 flex justify-between items-center">
            <h1 className="text-3xl font-bold">Administrador</h1>
            <button className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded">Cerrar Sesi√≥n</button>
          </div>
          <div className="flex flex-1">
            <div className="bg-gray-200 w-1/4 p-4">
              <ul>
                {options.map((option) => (
                  <li key={option.name}>
                    <button
                      className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded w-full text-left"
                      onClick={() => handleOptionClick(option.name)}
                    >
                      {option.name}
                    </button>
                    {showSubOptions && selectedOption === option.name && (
                      <ul className="pl-4">
                        {option.subOptions.map((subOption) => (
                          <li key={subOption}>
                            <button
                              className="bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-1 px-2 rounded w-full text-left text-sm"
                              onClick={() => handleSubOptionClick(subOption)}
                            >
                              {subOption}
                            </button>
                          </li>
                        ))}
                      </ul>
                    )}
                  </li>
                ))}
              </ul>
            </div>
            <div className="bg-white flex-1 p-4">
              {selectedSubOption === 'Crear' && selectedOption === 'Producto' && (
                <form>
                  <label className="block text-gray-700 text-sm font-bold mb-2">Nombre del Producto:</label>
                  <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="text" />
                  <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Crear</button>
                </form>
              )}
              {selectedSubOption === 'Editar' && selectedOption === 'Producto' && (
                <form>
                  <label className="block text-gray-700 text-sm font-bold mb-2">Nombre del Producto:</label>
                  <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="text" />
                  <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Editar</button>
                </form>
              )}
              {selectedSubOption === 'Eliminar' && selectedOption === 'Producto' && (
                <form>
                  <label className="block text-gray-700 text-sm font-bold mb-2">Nombre del Producto:</label>
                  <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="text" />
                  <button className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded">Eliminar</button>
                </form>
              )}
            </div>
          </div>
        </div>
      );
}
