import React, { useState } from 'react';

export const Menu = () => {
    const [activoSubmenu, setActiveSubmenu] = useState(null);

    const cambiarSubmenu = (submenu) => {
        setActiveSubmenu(activoSubmenu === submenu ? null : submenu);
    };

    return (
        <div>
            <ul className='ListaPrincipal'>
                <li>
                    <span onClick={()=>cambiarSubmenu('producto')}>Producto</span>
                    {activoSubmenu === 'producto' && (
                        <ul  className='ListaSecundaria'>
                            <li>Crear Producto</li>
                            <li>Modificar Producto</li>
                            <li>Consultar Producto</li>
                            <li>Eliminar Producto</li>
                        </ul>
                    )}
                </li>
                <li>
                    <span onClick={()=>cambiarSubmenu('inventario')}>Inventario</span>
                    {activoSubmenu === 'inventario' && (
                        <ul className='ListaSecundaria'>
                            <li>Reporte Inventario</li>
                            <li>Modificar Inventario</li>
                            <li>Consultar Inventario</li>
                        </ul>
                    )}
                </li>


                <li>
                    <span onClick={()=>cambiarSubmenu('movimientos')}>Movimientos</span>
                    {activoSubmenu === 'movimientos' && (
                        <ul className='ListaSecundaria'>
                            <li>Crear Movimientos</li>
                            <li>Modificar Movimientos</li>
                            <li>Consulta Movimientos</li>
                            <li>Eliminar Movimientos</li>
                        </ul>
                    )}
                </li>
                <li>
                    <span onClick={()=>cambiarSubmenu('usuarios')}>Usuarios</span>
                    {activoSubmenu === 'usuarios' && (
                        <ul className='ListaSecundaria'>
                            <li>Crear Usuario</li>
                            <li>Modificar Usuario</li>
                            <li>Eliminar Usuario</li>
                        </ul>
                    )}
                </li>
                <li>
                    <span onClick={()=>cambiarSubmenu('auditoria')}>Auditoria</span>
                    {activoSubmenu === 'auditoria' && (
                        <ul className='ListaSecundaria'>
                            <li>Consultar Auditoria</li>
                            <li>Modificar Auditoria</li>
                            <li>Costos por Almacenamiento</li>
                        </ul>
                    )}
                </li>
            </ul>
        </div>
    );
};
