import { useState } from "react"
import axios from "axios";

export const Usuario = ({nombre, largo}) =>{
    const [usuario,setUsuario] = useState("");
    const [clave,setClave] = useState("");
    
    function actualizarUsuario(e){
        setUsuario(e.target.value);
    }
    function actualizarClave(e){
        setClave(e.target.value);
    }
    async function intentoLogin(e){
        e.preventDefault();
        try {
            const response = await axios.get('/Login/verificar', {
                params: {
                    usuario: usuario,
                    clave: clave
                }
            });
            console.log("Respuesta del servidor:", response.data);
            // Aquí puedes manejar la respuesta, como redirigir al usuario o mostrar un mensaje
        } catch (error) {
            console.error("Error al verificar el usuario:", error);
            // Manejar el error, como mostrar un mensaje de error al usuario
        }
    }

    return <div className='usuario'>
        <h1>Inicio de sesion</h1>
        <form onSubmit={intentoLogin}>
            <div className="campo">
                Usuario: <input className="input" type="text" value={usuario} onChange={actualizarUsuario}/>
            </div>
            <div className="campo">
                Clave: <input className="input" type="password" value={clave} onChange={actualizarClave}/>
            </div>
        </form>
        <button type="submit">Entrar </button>
    </div>
}
