    //patrones para verificacion
    /*
    const patronPositivoDecimal = /^[+]?\d+(\.\d+)?$/;
    const patronPositivoEntero =/^[+]?\d+$/;
    */
    // Funciones para actualizar el estado de cada campo
    function actualizarIdProducto(e) {
        setIdProducto(e.target.value);
    }

    //envio del formulario
    async function envioConsultarProducto(e){
        e.preventDefault();
        try{
            const response=await axios.post('http://localhost:8080/Producto/consultaid',{id:{IdProducto}})
        }catch(error){

        }
    }

    return ( 
        <div>
            <form className='formConsultarProducto' onSubmit={envioConsultarProducto}>
                <div className="campo">
                    <label className="label1">Id del producto a Consultar:</label> 
                    <input className="input" type="number" value={IdProducto} onChange={actualizarIdProducto} />
                </div>
                <button type='submit'>Enviar</button>
            </form> 

        </div>
    );
};
